import React, { useState } from 'react'
import './ProductPanierCard.css'; 
const ProductPanierCard = ({product ,onQuantityChange }) => {
  const [quantity, setQuantity] = useState(product.quantity);
  
  const handleQuantityChange = (e) => {
    const newQuantity = Math.max(0, e.target.value); // Ensure quantity is not negative
    setQuantity(newQuantity);
    onQuantityChange(product.id, newQuantity); // Notify parent about the quantity change
  };
  const totalPrice = product.price * product.quantity;


  return (
    <div className="card">
      <img src={product.poster} alt="Product not found" />
      <div className="info">
        <p className="description">{product.description}</p>
        <p className="price">Price: {product.price} Dt</p>
        {/* <p className="quantity">Quantity: {product.quantity}</p>
        <p className="total">Total: {totalPrice} Dt</p> */}
        <input
        type="number"
          value={quantity}
          onChange={handleQuantityChange}
          min="0"
          className="quantity-input"
        />
        <p className="total">Total: {totalPrice} Dt</p>


        </div>
    </div>
  )
}

export default ProductPanierCard
